package com.github.cfogrady.vbnfc.be

import com.github.cfogrady.vbnfc.ChecksumCalculator
import com.github.cfogrady.vbnfc.CryptographicTransformer
import com.github.cfogrady.vbnfc.data.NfcCharacter
import io.mockk.mockkClass
import org.junit.Assert
import org.junit.Test

class BENfcDataTranslatorTest {
    @OptIn(ExperimentalStdlibApi::class)
    @Test
    fun testNfcCharacterParsing() {
        val nfcBytes = "".hexToByteArray()
        val mockCryptographicTransformer = mockkClass(CryptographicTransformer::class)
        val checksumCalculator = ChecksumCalculator()
        val beNfcDataTranslator = BENfcDataTranslator(mockCryptographicTransformer, checksumCalculator)

        val character = beNfcDataTranslator.parseNfcCharacter(nfcBytes)
        Assert.assertEquals(130, character.dimId)
        Assert.assertEquals(5, character.charIndex)
        Assert.assertEquals(3, character.phase)
        Assert.assertEquals(NfcCharacter.Attribute.Data, character.attribute)
        Assert.assertEquals(0, character.ageInDays)
        Assert.assertEquals(100, character.mood)
        Assert.assertEquals(1, character.characterCreationFirmwareVersion.majorVersion)
        Assert.assertEquals(1, character.characterCreationFirmwareVersion.minorVersion)
        Assert.assertEquals(4, character.nextAdventureMissionStage)
        Assert.assertEquals(1204, character.vitalPoints)
        Assert.assertEquals(776, character.transformationCountdown)
        Assert.assertEquals(NfcCharacter.InjuryStatus.None, character.injuryStatus)
    }
}